% Mouse specific Parameters for RRTask

switch Log.Mouse
    case 'A'
        Par.GoFigOrient = 0;
        Par.GoBgOrient = Par.GoFigOrient + 90;
        Par.NoGoFigOrient = 45;
        Par.NoGoBgOrient = Par.NoGoFigOrient + 90;
        
    case 'B'
        Par.GoFigOrient = 0;
        Par.GoBgOrient = Par.GoFigOrient + 90;
        Par.NoGoFigOrient = 45;
        Par.NoGoBgOrient = Par.NoGoFigOrient + 90;
        
    case 'C'
        Par.GoFigOrient = 0;
        Par.GoBgOrient = Par.GoFigOrient + 90;
        Par.NoGoFigOrient = 45;
        Par.NoGoBgOrient = Par.NoGoFigOrient + 90;
        
end
        